import { useEffect, useState } from "react";
import { getClients } from "./util/getClientes";
import * as React from "react";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell, { tableCellClasses } from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import Button from "@mui/material/Button";
import DeleteIcon from "@mui/icons-material/Delete";
import Modal from "@mui/material/Modal";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";

function Clientes({ setClients, clients }) {
  useEffect(() => {
    getClients(setClients);
  }, []);

  function deleteClient(id) {
    fetch(`https://facec-webapi-2022.herokuapp.com/clientes/${id}`, {
      cache: "no-cache",
      method: "DELETE",
      mode: "cors",
    })
      .then(function (res) {
        console.log(res);
        return res.json();
      })
      .then((data) => {
        console.log(data);
        // setClients(data);
      })
      .catch((err) => console.log(err));
  }
  const StyledTableCell = styled(TableCell)(({ theme }) => ({
    [`&.${tableCellClasses.head}`]: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white,
    },
    [`&.${tableCellClasses.body}`]: {
      fontSize: 14,
    },
  }));

  const StyledTableRow = styled(TableRow)(({ theme }) => ({
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover,
    },
    // hide last border
    "&:last-child td, &:last-child th": {
      border: 0,
    },
  }));
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 400,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  return (
    <div className="text-xl text-zinc-100 border border-zinc-700 bg-gradient-to-r from-zinc-800 to-zinc-900 bg-opacity-40 h-full w-full rounded-xl p-6 mx-h-[1024px] overflow-auto scrollbar-thin scrollbar-track-zinc-700">
      <div className="table">
        <TableContainer component={Paper}>
          <Table sx={{ minWidth: 700 }} aria-label="customized table">
            <TableHead>
              <TableRow>
                <StyledTableCell>Numero</StyledTableCell>
                <StyledTableCell>Nome</StyledTableCell>
                <StyledTableCell>Documento</StyledTableCell>
                <StyledTableCell>
                  <span className="sr-only">Delete</span>
                </StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {clients &&
                clients.map((client, i) => {
                  return (
                    <StyledTableRow key={i}>
                      <StyledTableCell>{i + 1}</StyledTableCell>
                      <StyledTableCell>{client.nome}</StyledTableCell>
                      <StyledTableCell>{client.documento} </StyledTableCell>
                      <StyledTableCell>
                        <Button
                          variant="outlined"
                          onClick={() => deleteClient(client.id)}
                          startIcon={<DeleteIcon />}
                        >
                          Delete
                        </Button>
                      </StyledTableCell>
                    </StyledTableRow>
                  );
                })}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    </div>
  );
}
export default Clientes;
